import java.util.Arrays;import java.util.Random;public class Ex_2 {    /* Создайте массив из 5 случайных целых чисел из интервала [10;99]    Выведите его на консоль в строку.    Определите и выведите на экран сообщение о том, является ли массив строго возрастающей последовательностью. */    public static void main(String[] args) {        Random random = new Random();        Integer[] arr = new Integer[5];        for (int i = 0; i < arr.length; i++) {            arr[i] = random.nextInt(10, 99);        }        System.out.println(Arrays.toString(arr));        for (int i = 0; i < 1; i++) {            if(arr[i] < arr[i + 1] && arr[i + 1] < arr[i + 2] && arr[i + 2] < arr[i + 3] && arr[i + 3] < arr[i +4]) {                System.out.println("Массив является строго возрастающей последовательностью.");            } else {                System.out.println("Массив не является строго возрастающей последовательностью.");            }        }    }}