import java.util.Arrays;import java.util.Random;import java.util.Scanner;public class Ex_3 {    public static void main(String[] args) {        /* Дополнительно:    Дан массив размера  N-1  , содержащий только различные целые числа в диапазоне от  1 до N . Найдите    недостающий элемент.    Пример 1:    Вход:    N = 5    А[] = {1,2,3,5}    Выход: 4    Пример 2:    Вход:    N = 10    А[] = {6,1,2,8,3,4,7,10,5}    Выход: 9 */        Scanner sc = new Scanner(System.in);        System.out.print("Введите длину массива ");        Integer[] arr = new Integer[sc.nextInt()];        Random random = new Random();        System.out.print("Введите диапазон целых чисел от ");        int rangeFrom = sc.nextInt();        System.out.print("Введите целое число до ");        int rangeUpTo = sc.nextInt();        for (int i = 0; i < arr.length; i++) {            arr[i] = random.nextInt(rangeFrom, rangeUpTo);        }        System.out.println(Arrays.toString(arr));        Arrays.sort(arr);        System.out.println(Arrays.toString(arr));        int num1;        int num2;        for (int i = 0; i < arr.length - 1; i++) {            if (arr[i] != (arr[i + 1] - 1)) {                num1 = arr[i];                num2 = arr[i + 1];                while (num1 != num2 && num1 != num2 - 1) {                    num1++;                    System.out.println("В массиве не хватает: " + num1);                }            }        }    }}